import React, {useState} from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  ScrollView,
  Alert,
} from 'react-native';
import {useApp} from '../context/AppContext';

const SignUpOwnerScreen = ({navigation}: any) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [errors, setErrors] = useState<{email?: string; password?: string; name?: string}>({});
  const [touched, setTouched] = useState<{email?: boolean; password?: boolean; name?: boolean}>({});
  const {signUpOwner} = useApp();

  const validateEmail = (email: string): string | undefined => {
    if (!email) {
      return 'Email is required';
    }
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      return 'Please enter a valid email address';
    }
    return undefined;
  };

  const validatePassword = (password: string): string | undefined => {
    if (!password) {
      return 'Password is required';
    }
    if (password.length < 6) {
      return 'Password must be at least 6 characters';
    }
    if (password.length > 100) {
      return 'Password must be less than 100 characters';
    }
    return undefined;
  };

  const validateName = (name: string): string | undefined => {
    // Name is optional, but if provided, validate it
    if (name.trim()) {
      if (name.trim().length < 2) {
        return 'Name must be at least 2 characters';
      }
      if (name.trim().length > 50) {
        return 'Name must be less than 50 characters';
      }
      // Check for invalid characters (only letters, spaces, hyphens, apostrophes)
      const nameRegex = /^[a-zA-Z\s'-]+$/;
      if (!nameRegex.test(name.trim())) {
        return 'Name can only contain letters, spaces, hyphens, and apostrophes';
      }
    }
    return undefined;
  };

  const handleEmailChange = (text: string) => {
    setEmail(text);
    if (touched.email) {
      setErrors({...errors, email: validateEmail(text)});
    }
  };

  const handlePasswordChange = (text: string) => {
    setPassword(text);
    if (touched.password) {
      setErrors({...errors, password: validatePassword(text)});
    }
  };

  const handleNameChange = (text: string) => {
    setName(text);
    if (touched.name) {
      setErrors({...errors, name: validateName(text)});
    }
  };

  const handleBlur = (field: 'email' | 'password' | 'name') => {
    setTouched({...touched, [field]: true});
    let error: string | undefined;
    if (field === 'email') {
      error = validateEmail(email);
      setErrors({...errors, email: error});
      if (error) {
        Alert.alert('Validation Error', error);
      }
    } else if (field === 'password') {
      error = validatePassword(password);
      setErrors({...errors, password: error});
      if (error) {
        Alert.alert('Validation Error', error);
      }
    } else if (field === 'name') {
      error = validateName(name);
      setErrors({...errors, name: error});
      if (error) {
        Alert.alert('Validation Error', error);
      }
    }
  };

  const handleSignUp = () => {
    const emailError = validateEmail(email);
    const passwordError = validatePassword(password);
    const nameError = validateName(name);
    
    setTouched({email: true, password: true, name: true});
    setErrors({
      email: emailError,
      password: passwordError,
      name: nameError,
    });

    if (emailError || passwordError || nameError) {
      // Show the first error in a popup
      const firstError = emailError || passwordError || nameError;
      Alert.alert('Validation Error', firstError);
      return;
    }

    if (signUpOwner(email, password, name)) {
      // Redirect directly to login after successful sign up
      Alert.alert('Success', 'Account created successfully! Please login.', [
        {
          text: 'OK',
          onPress: () => navigation.replace('Login'),
        },
      ]);
      // Auto-redirect after 1 second if user doesn't dismiss alert
      setTimeout(() => {
        navigation.replace('Login');
      }, 1500);
    } else {
      setErrors({...errors, email: 'This email is already registered'});
      Alert.alert('Registration Error', 'An account with this email already exists');
    }
  };

  const navigateToLogin = () => {
    navigation.navigate('Login');
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        {/* App Title */}
        <Text style={styles.appTitle}>V.E.T. App</Text>

        {/* Section Header */}
        <Text style={styles.sectionHeader}>Create an account</Text>

        {/* Instructions */}
        <Text style={styles.instructions}>
          Enter your information to sign up as a pet owner
        </Text>

        {/* Name Input (Optional) */}
        <View>
          <TextInput
            style={[
              styles.input,
              touched.name && errors.name && styles.inputError,
            ]}
            placeholder="Name (Optional)"
            placeholderTextColor="#999999"
            value={name}
            onChangeText={handleNameChange}
            onBlur={() => handleBlur('name')}
            autoCapitalize="words"
          />
        </View>

        {/* Email Input */}
        <View>
          <TextInput
            style={[
              styles.input,
              touched.email && errors.email && styles.inputError,
            ]}
            placeholder="email@domain.com"
            placeholderTextColor="#999999"
            value={email}
            onChangeText={handleEmailChange}
            onBlur={() => handleBlur('email')}
            keyboardType="email-address"
            autoCapitalize="none"
            autoCorrect={false}
          />
        </View>

        {/* Password Input */}
        <View>
          <TextInput
            style={[
              styles.input,
              touched.password && errors.password && styles.inputError,
            ]}
            placeholder="Password (min. 6 characters)"
            placeholderTextColor="#999999"
            value={password}
            onChangeText={handlePasswordChange}
            onBlur={() => handleBlur('password')}
            secureTextEntry
            autoCapitalize="none"
            autoCorrect={false}
          />
        </View>

        {/* Sign Up Button */}
        <TouchableOpacity style={styles.signUpButton} onPress={handleSignUp}>
          <Text style={styles.signUpButtonText}>Sign Up</Text>
        </TouchableOpacity>

        {/* Login Link */}
        <TouchableOpacity onPress={navigateToLogin} style={styles.loginLink}>
          <Text style={styles.loginText}>
            Already have an account? <Text style={styles.loginLinkText}>Login</Text>
          </Text>
        </TouchableOpacity>

        {/* Legal Disclaimer */}
        <Text style={styles.disclaimer}>
          By clicking sign up, you agree to our{' '}
          <Text style={styles.link}>Terms of Service</Text> and{' '}
          <Text style={styles.link}>Privacy Policy</Text>
        </Text>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFFFFF',
  },
  scrollContent: {
    flexGrow: 1,
    paddingHorizontal: 24,
    paddingTop: 60,
    paddingBottom: 40,
  },
  appTitle: {
    fontSize: 32,
    fontWeight: 'bold',
    color: '#000000',
    textAlign: 'center',
    marginBottom: 8,
  },
  sectionHeader: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#000000',
    textAlign: 'center',
    marginBottom: 8,
  },
  instructions: {
    fontSize: 16,
    color: '#666666',
    textAlign: 'center',
    marginBottom: 32,
  },
  input: {
    backgroundColor: '#FFFFFF',
    borderWidth: 1,
    borderColor: '#E0E0E0',
    borderRadius: 12,
    paddingHorizontal: 16,
    paddingVertical: 16,
    fontSize: 16,
    marginBottom: 4,
  },
  inputError: {
    borderColor: '#FF3B30',
    borderWidth: 1.5,
  },
  signUpButton: {
    backgroundColor: '#000000',
    borderRadius: 12,
    paddingVertical: 16,
    alignItems: 'center',
    marginTop: 8,
    marginBottom: 20,
  },
  signUpButtonText: {
    color: '#FFFFFF',
    fontSize: 16,
    fontWeight: '600',
  },
  loginLink: {
    alignItems: 'center',
    marginBottom: 20,
  },
  loginText: {
    fontSize: 14,
    color: '#000000',
  },
  loginLinkText: {
    color: '#007AFF',
    fontWeight: '600',
  },
  disclaimer: {
    fontSize: 12,
    color: '#666666',
    textAlign: 'center',
    marginTop: 20,
    lineHeight: 18,
  },
  link: {
    color: '#007AFF',
    textDecorationLine: 'underline',
  },
});

export default SignUpOwnerScreen;
